import re, datetime, requestsfrom bs4 import BeautifulSoupURL = "https://www.chris.org/cgi-bin/jt65emeA"html = requests.get(URL, timeout=30).texttext = BeautifulSoup(html, "html.parser").get_text()lines = [line.rstrip() for line in text.splitlines()]# line parser:  "10Sep 19:34 <message> ====== {<meta>}"line_rx = re.compile(r"""^\s*    (?P<daymon>\d{2}[A-Za-z]{3})\s+    (?P<time>\d{2}:\d{2})\s+    (?P<message>.*?)\s+    ======\s+\{(?P<meta>.*?)\}\s*$""", re.X)# meta helperscallsign_rx = re.compile(r"^[A-Z0-9]+", re.I)grid_rx = re.compile(r"\b([A-R]{2}\d{2})", re.I)  # e.g., JO33, RE68entries = []for line in lines:    m = line_rx.match(line)    if not m:        continue    daymon, t, msg, meta = m.group("daymon", "time", "message", "meta")    daymon = daymon[:2] + " " + daymon[2:]    # parse meta ? callsign, name, grid (best-effort)    call = (callsign_rx.search(meta).group(0) if callsign_rx.search(meta) else None)    grid_match = grid_rx.search(meta)    grid = grid_match.group(1) if grid_match else None    # crude name extraction: remove callsign & trailing grid-like token    if call:        tmp = meta[len(call):].strip(" /")        if grid:            tmp = re.sub(rf"\b{re.escape(grid)}\b\s*$", "", tmp).strip()        # drop stray "xx" tags and obvious hardware notes        tmp = re.sub(r"\bxx\b", "", tmp).strip()    entries.append({        "utc": daymon + " " + t,        "message": msg.strip(),        "sender": meta.strip(),        "call": call,        "grid": grid    })for line in entries:    print(line["utc"], line["message"], line["call"], line["grid"])print(f"parsed {len(entries)} lines")